import React from "react";

export default class AutoCompletText extends React.Component {
  constructor(props) {
    super(props);
    // this.data = ["Toto", "Dani", "Damien", "Shadd", "Jean"];
    this.data = [
      "B18 - Hépatite virale chronique",
      "B180 - Hépatite virale chronique B avec agent delta",
      "B181 - Hépatite virale chronique B sans agent delta",
      "B182 - Hépatite virale chronique C",
      "B188 - Autres hépatites virales chroniques",
      "B189 - Hépatite virale chronique, sans précision",
      "-B19 - Hépatite virale, sans précision"
    ];
    this.state = {
      suggesstions: [],
      text: ""
    };
  }

  textChange = e => {
    const value = e.target.value;
    let suggesstions = [];

    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, "i");
      suggesstions = this.data.sort().filter(v => regex.test(v));
    }
    this.setState({
      suggesstions,
      text: value
    });
  };

  suggesstionsSelected = value => {
    this.setState({
      text: value,
      suggesstions: []
    });
  };

  renderSuggestions = () => {
    if (this.state.suggesstions.length === 0) {
      return null;
    }
    return (
      <ul>
        {this.state.suggesstions.map(data => (
          <li onClick={() => this.suggesstionsSelected(data)}>{data}</li>
        ))}
      </ul>
    );
  };

  render() {
    const { selectedCheckbox } = this.props;

    return (
      <div>
        <input
          value={this.state.text}
          onChange={this.textChange}
          id="formGroupExampleInput"
          placeholder={selectedCheckbox[0].diag}
          type="text"
        />
        {this.renderSuggestions()}
      </div>
    );
  }
}
