import React from "react";

export default class AutoCompletText extends React.Component {
  constructor(props) {
    super(props);
    this.data = ["Toto", "Dani", "Damien", "Shadd", "Jean"];
    this.state = {
      suggesstions: [],
      text: ""
    };
  }

  textChange = e => {
    const value = e.target.value;
    let suggesstions = [];

    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, "i");
      suggesstions = this.data.sort().filter(v => regex.test(v));
    }
    this.setState({
      suggesstions,
      text: value
    });
  };

  suggesstionsSelected = value => {
    this.setState({
      text: value,
      suggesstions: []
    });
  };

  selectedCheckbox = () => {
    var Test = "";
    if (this.props.selectedCheckbox[0]) {
      Test = this.props.selectedCheckbox[0]["diag"];
    }
  };

  renderSuggestions = () => {
    if (this.state.suggesstions.length === 0) {
      return null;
    }
    return (
      <ul>
        {this.state.suggesstions.map(data => (
          <li onClick={() => this.suggesstionsSelected(data)}>{data}</li>
        ))}
      </ul>
    );
  };

  render() {
    const { selectedCheckbox } = this.props;
    console.log("this.props", selectedCheckbox);
    return (
      <div>
        <input
          value={this.state.text}
          onChange={this.textChange}
          placeholder={this.selectedCheckbox()}
          type="text"
        />
        {this.renderSuggestions()}
      </div>
    );
  }
}
